package com.cloudcrunch.hdfc.banking.risk.test.Equals;

public class Table {

	private int id;
	private double size;
	
	
	public Table() {
		super();
		// TODO Auto-generated constructor stub
	}
	public Table(int id, double size) {
		super();
		this.id = id;
		this.size = size;
	}
	public int getId() {
		return id;
	}
	public void setId(int id) {
		this.id = id;
	}
	public double getSize() {
		return size;
	}
	public void setSize(double size) {
		this.size = size;
	}
	@Override
	public String toString() {
		return "Table [id=" + id + ", size=" + size + "]";
	}
	
	@Override
	public boolean equals(Object obj) {
		if (this == obj)
			return true;
		if (obj == null)
			return false;
		if (getClass() != obj.getClass())
			return false;
		Table other = (Table) obj;
		if (id != other.id)
			return false;
		if (Double.doubleToLongBits(size) != Double.doubleToLongBits(other.size))
			return false;
		return true;
	}

	
}
